 * @public
 */
export const ErrKeyNotFound = new Error('Requested key was not present in storage')

/**
 * The interface for local key-value storage.
 * @public
 */
export interface Storage {
  /**
   * Retrieves a key from the storage.
   *
   * @param key - the key to retrieve
   */
  get(key: string): string | null

  /**
   * Adds a key to the storage, possibly overwriting existing value.
   *
   * @param key - the key to write
   * @param val - the value to write
   */
  set(key: string, val: string): void

  /**
   * Adds a key to the storage, possibly overwriting existing value.
   *
   * @param key - the key to write
   * @param genFn - generator function that will be invoked if the key cannot be found in the storage.
   * The return value of the function will be used as the value that will be stored under the given key.
   */
  getOrSet(key: string, genFn: () => string): string
}
import { Word, Entity, Intent, Segment } from './types'

export class SegmentState {
  id: number
  contextId: string
  isFinalized: boolean = false
  words: Word[] = []
  entities: Map<string, Entity> = new Map<string, Entity>()
  intent: Intent = { intent: '', isFinal: false }

  constructor(ctxId: string, sId: number) {
    this.contextId = ctxId
    this.id = sId
  }

  toSegment(): Segment {
    let i = 0
    const entities: Entity[] = new Array(this.entities.size)
    this.entities.forEach(v => {
      entities[i] = v
      i++
    })

    return {
      id: this.id,
      contextId: this.contextId,
      isFinal: this.isFinalized,
      words: this.words,
      entities: entities,
      intent: this.intent,
    }
  }

  toString(): string {
    const segment: Segment = this.toSegment()
    const words = segment.words.filter((w: Word) => w.value).map((w: Word) => ({ value: w.value, index: w.index }))
    const cleanSegment = { ...segment, ...{ words } }
    return JSON.stringify(cleanSegment, null, 2)
  }

  updateTranscript(words: Word[]): SegmentState {
    words.forEach(w => {
      // Only accept tentative words if the segment is tentative.
      if (!this.isFinalized || w.isFinal) {
        this.words[w.index] = w
      }
    })

    return this
  }

  updateEntities(entities: Entity[]): SegmentState {
    entities.forEach(e => {
      // Only accept tentative entities if the segment is tentative.
      if (!this.isFinalized || e.isFinal) {
        this.entities.set(entityMapKey(e), e)
      }
    })
    return this
  }

  updateIntent(intent: Intent): SegmentState {
    // Only accept tentative intent if the segment is tentative.
    if (!this.isFinalized || intent.isFinal) {
      this.intent = intent
    }

    return this
  }

  finalize(): SegmentState {
    // Filter away any entities which were not finalized.
    this.entities.forEach((val, key) => {
      if (!val.isFinal) {
        this.entities.delete(key)
      }
    })

    // Filter away any transcripts which were not finalized.
    this.words = this.words.filter(w => w.isFinal)

    if (!this.intent.isFinal) {
      this.intent.intent = ''
      this.intent.isFinal = true
    }

    // Mark as final.
    this.isFinalized = true

    return this
  }
}

function entityMapKey(e: Entity): string {
  return `${e.startPosition.toString()}:${e.endPosition.toString()}`
}
import {
  TentativeTranscriptResponse,
  TranscriptResponse,
  TentativeEntitiesResponse,
  EntityResponse,
  IntentResponse,
} from '../websocket'
import { Word, Entity, Intent } from './types'

export function parseTentativeTranscript(data: TentativeTranscriptResponse): Word[] {
  return data.words.map(({ word, index, start_timestamp, end_timestamp }) => {
    return {
      value: word,
      index: index,
      startTimestamp: start_timestamp,
      endTimestamp: end_timestamp,
      isFinal: false,
    }
  })
}

export function parseTranscript(data: TranscriptResponse): Word {
  return {
    value: data.word,
    index: data.index,
    startTimestamp: data.start_timestamp,
    endTimestamp: data.end_timestamp,
    isFinal: true,
  }
}

export function parseTentativeEntities(data: TentativeEntitiesResponse): Entity[] {
  return data.entities.map(({ entity, value, start_position, end_position }) => {
    return {
      type: entity,
      value: value,
      startPosition: start_position,
      endPosition: end_position,
      isFinal: false,
    }
  })
}

export function parseEntity(data: EntityResponse): Entity {
  return {
    type: data.entity,
    value: data.value,
    startPosition: data.start_position,
    endPosition: data.end_position,
    isFinal: true,
  }
}

export function parseIntent(data: IntentResponse, isFinal: boolean): Intent {
  return {
    intent: data.intent,
    isFinal: isFinal,
  }
}
